// RegistrationForm.js
import React, { useState } from 'react';

function RegistrationForm({ addUser }) {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    bloodGroup: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addUser(formData);
    setFormData({ firstName: '', lastName: '', email: '', bloodGroup: '' });
  };

  return (
    <form onSubmit={handleSubmit} style={styles.form}>
      <h2 style={styles.heading}>Register for Blood Donation</h2>
      <label style={styles.label}>
        First Name:
        <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} style={styles.input} />
      </label>
      <br />
      <label style={styles.label}>
        Last Name:
        <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} style={styles.input} />
      </label>
      <br />
      <label style={styles.label}>
        Email:
        <input type="email" name="email" value={formData.email} onChange={handleChange} style={styles.input} />
      </label>
      <br />
      <label style={styles.label}>
        Blood Group:
        <input type="text" name="bloodGroup" value={formData.bloodGroup} onChange={handleChange} style={styles.input} />
      </label>
      <br />
      <button type="submit" style={styles.button}>Submit</button>
    </form>
  );
}

const styles = {
  form: {
    marginBottom: '40px',
  },
  heading: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  label: {
    display: 'block',
    marginBottom: '10px',
  },
  input: {
    width: '100%',
    padding: '8px',
    marginBottom: '10px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  button: {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default RegistrationForm;
